      XueChengPlusException.cast(&lt;span class=&#34;string&#34;&gt;&amp;quot;订单不存在&amp;quot;&lt;/span&gt;); &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125; &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt;(orders.getStatus().equals(&lt;span class=&#34;string&#34;&gt;&amp;quot;600002&amp;quot;&lt;/span&gt;))&amp;#123; &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        XueChengPlusException.cast(&lt;span class=&#34;string&#34;&gt;&amp;quot;订单已支付&amp;quot;&lt;/span&gt;); &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125; &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;type&#34;&gt;XcPayRecord&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;payRecord&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; &lt;span class=&#34;title class_&#34;&gt;XcPayRecord&lt;/span&gt;(); &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;//生成支付交易流水号 &lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;type&#34;&gt;long&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;payNo&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; IdWorkerUtils.getInstance().nextId(); &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    payRecord.setPayNo(payNo); &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    payRecord.setOrderId(orders.getId());&lt;span class=&#34;comment&#34;&gt;//商品订单号 &lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    payRecord.setOrderName(orders.getOrderName()); &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    payRecord.setTotalPrice(orders.getTotalPrice()); &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    payRecord.setCurrency(&lt;span class=&#34;string&#34;&gt;&amp;quot;CNY&amp;quot;&lt;/span&gt;); &lt;/span&gt;&